import React from 'react';
import audio from '../../logos/audio.png';

 class SpeechComp extends React.Component {
    speechHandler =()=>{
        const utterThis = new SpeechSynthesisUtterance("Zayba Tehseen");
        window.speechSynthesis.speak(utterThis);
    }
  render() {
    return (
        <div>
            <h2>Zeba Tahseen</h2><img src={audio} alt="" onClick={this.speechHandler}/>
        </div>
    )
  }
}
