//       <div>WorkExp</div>
//     )
//   }
// }
class WorkExp extends React.Component {
    constructor(props) {
      super(props)

      this.state = {
         formattedData:[]
      }
    }
    displayTable(data){
        this.setState({formattedData:data});
    }

render(){
    const [data, setData] = useState(null);
    const columnNames=[
        "Order",
        "CompanyName",
        "Experience",
        "Position"
      ];

    useEffect(() => {
      fetch('https://mocki.io/v1/e0440544-4ef9-4a94-9729-ea4ccd0b3250')
        .then(response => response.json())
        .then(json => setData(json))
        .catch(error => console.error(error));
    }, []);

    return (
        <div>
           {data ? <pre>{JSON.stringify(data, null, 2)}</pre> : 'Loading...'}
            <button onClick={this.displayTable(data)}>Format</button>
            <WorkExpFormatted tableData={this.state.formattedData} columnNames={columnNames}/>
        </div>

    )
}
}

export default WorkExp;
