import React from 'react';
import WorkExpFormat from './WorkExpFormat';

class WorkExp extends React.Component {
    constructor(props) {
      super(props)

      this.state = {
         formattedData:[],
         showTable:false
      }
      this.displayTable = this.displayTable.bind(this);
    }
    displayTable(data){
        this.setState({formattedData:data, showTable:true});
    }


render(){

    return (
        <div>
           {this.props.data ? <pre>{JSON.stringify(this.props.data, null, 2)}</pre> : 'Loading...'}
            <button onClick={this.displayTable}>Format</button>
            <WorkExpFormat showTable={this.state.showTable} tableData={this.state.formattedData} columnNames={this.props.columnNames}/>
        </div>

    )
}
}

export default WorkExp;
